INSERT INTO `company` (`id`, `nameCompany`, `officeAddress`, `logo`, `website`, `aboutCompany`, `scale`, `career_id`, `welfare_id`, `vip`, `created_at`, `updated_at`) VALUES
	(1, 'Colonial Stores', '1', 'http://findjob.test/frontend/image-recruiment-logo/1638288516.png', 'abc.com', '<p style="margin-bottom: 0px; padding-bottom: 1em; overflow-wrap: break-word; font-size: 15px; font-family: &quot;Roboto,Helvetica,Verdana,Arial&quot;, sans-serif; line-height: 26px; color: rgb(57, 62, 70); text-align: justify;"><span style="overflow-wrap: break-word; font-family: Roboto, Helvetica, Verdana, Arial, sans-serif;">Keizu Việt Nam là một công ty 100% vốn đầu tư từ Nhật Bản, hoạt động trong lĩnh vực sản xuất sản phẩm phần mềm. Là một trong những công ty chuyên hợp tác phát triển offshore và cung cấp dịch vụ Salesforce CRM cho các khách hàng sử dụng Cloud Computing tại thị trường Nhật Bản và Việt Nam.&nbsp;</span></p><p style="margin-bottom: 0px; padding-bottom: 1em; overflow-wrap: break-word; font-size: 15px; font-family: &quot;Roboto,Helvetica,Verdana,Arial&quot;, sans-serif; line-height: 26px; color: rgb(57, 62, 70); text-align: justify;"><span style="overflow-wrap: break-word; font-family: Roboto, Helvetica, Verdana, Arial, sans-serif;">Chúng tôi luôn chào đón các kỹ sư IT tương lai mong muốn trải nghiệm môi trường làm việc doanh nghiệp Nhật Bản, cũng như tuyển dụng chính thức các sinh viên tài năng với chế độ đãi ngộ tốt nhất.</span></p>', 2, '["1","2","4"]', '["1","2","3"]', 1, NULL, '2021-11-30 16:58:38'),
	(2, 'Công ty TNHH Bitis THượng Đình', '1', 'http://findjob.test/frontend/image-recruiment-logo/1638288979.png', 'thanglong.com', '<h6 class="fz17 mb-2" style="font-size: 17px; overflow-wrap: break-word; font-family: &quot;Roboto,Helvetica,Verdana,Arial&quot;, sans-serif; color: rgb(57, 62, 70);">"EASY PEASY GLOBAL BUSINESS"</h6><div style="overflow-wrap: break-word; font-size: 14px; font-family: &quot;Roboto,Helvetica,Verdana,Arial&quot;, sans-serif; color: rgb(57, 62, 70);"><p style="margin-bottom: 0px; padding-bottom: 1em; overflow-wrap: break-word; font-size: 15px; line-height: 26px; text-align: justify;">"DOANH NGHIỆP TOÀN CẦU EASY PEASY” chứa đựng những mong muốn của “supply” và “seller”! (Hệ thống bán hàng tương tự kênh bàn hàng của tập đoàn Alibaba)</p><p style="margin-bottom: 0px; padding-bottom: 1em; overflow-wrap: break-word; font-size: 15px; line-height: 26px; text-align: justify;">Damda là thị trường mở kiểu dịch vụ D2C (= B2b2C) giúp bán hàng và tiếp thị toàn cầu dễ dàng bằng cách kết nối trực tiếp "supply" và "people" của mỗi quốc gia và là nền tảng mua hàng trực tiếp toàn cầu dựa trên SNS. Trên thị trường phân phối hiện nay, cả thị trường ngoại tuyến và trực tuyến DAMDA hướng tới một hệ sinh thái cộng sinh, hợp tác gắn kết giữa (supply - seller- costomer)&nbsp;</p><p style="margin-bottom: 0px; padding-bottom: 1em; overflow-wrap: break-word; font-size: 15px; line-height: 26px; text-align: justify;">“Damda People” cho phép bạn mở cửa hàng độc quyền của riêng mình mà không cần vốn, hàng tồn kho, nhân viên vận hành, giao hàng ,,vv,, qua nền tảng DAMDA (có thể lập cửa hàng miễn phí trên hệ thống của DAMDA). Ngoài ra, "Damda people ” có thể chọn các sản phẩm khác nhau từ mỗi quốc gia trong DAMDA, đưa chúng vào cửa hàng của riêng họ một cách dễ dàng và thuận tiện rồi thương mại hóa chúng.&nbsp; Nền tảng Damda hỗ trợ tiếp thị dễ dàng bằng cách liên kết nội dung SNS của "Damda People" với "Damda".&nbsp;</p><p style="margin-bottom: 0px; padding-bottom: 1em; overflow-wrap: break-word; font-size: 15px; line-height: 26px; text-align: justify;">“Các nhà cung cấp” có thể gặp gỡ “people Damda” của mỗi quốc gia mà họ tham gia thông qua nền tảng DAMDA và “Damda people ” cho phép họ gia nhập thị trường toàn cầu và tiến hành tiếp thị toàn cầu. Ngoài ra, "nhà cung cấp" có thể trải nghiệm các cơ hội phát triển thành một thương hiệu lành mạnh bằng cách thu hút khách hàng tiềm năng, tăng doanh số bán hàng, quản lý giá sản phẩm và duy trì định vị thương hiệu.&nbsp;</p><p style="margin-bottom: 0px; padding-bottom: 1em; overflow-wrap: break-word; font-size: 15px; line-height: 26px; text-align: justify;">Nền tảng Damda hướng tới một mô hình kinh doanh phi tập trung, mong đợi sự tăng trưởng và nhảy vọt trong kinh doanh của "Nhà cung cấp" và "Damda people" thông qua hệ thống "Damda".&nbsp; dưới đây là website chi tiết: https://company.damda.com http:// kr.damda.com</p></div>', 1, '["2","4"]', '["1","2","5","6"]', 0, NULL, '2021-11-30 16:16:19'),
